{% load static tailwind_tags %}
<!DOCTYPE html>
<html lang="en">
<head>
    <!-- Required meta tags -->
    <meta charset="utf-8" />
    <meta http-equiv="X-UA-Compatible" content="IE=edge" />
    <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no" />
    {% if title %}
    <title>{{ title }}</title>
    {% else %}
    <title>Trivivo</title>
    {% endif %}

    {% tailwind_css %}
    <link rel="icon" type="image/x-icon" href="{% static 'img/wwbmicon.ico' %}">
    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.4/jquery.min.js"></script>

</head>

<body class="bg-black text-white min-h-screen flex flex-col">
    <div id="smooth-grid" class="fixed inset-0 z-0"></div>
    {% block header %}{% endblock %}

    <div class="flex flex-col w-full relative z-10">
        {% if messages %}
        {% for message in messages %}
        <div
            {% if message.tags %}
                {% if message.tags == 'debug' %}
                    class="bg-gray-700 text-white mx-5 my-2 md:mx-auto md:w-[50rem] p-4 rounded flex justify-between items-center"
                {% elif message.tags == 'info' %}
                    class="bg-blue-700 text-white mx-5 my-2 md:mx-auto md:w-[50rem] p-4 rounded flex justify-between items-center"
                {% elif message.tags == 'success' %}
                    class="bg-green-700 text-white mx-5 my-2 md:mx-auto md:w-[50rem] p-4 rounded flex justify-between items-center"
                {% elif message.tags == 'warning' %}
                    class="bg-yellow-700 text-white mx-5 my-2 md:mx-auto md:w-[50rem] p-4 rounded flex justify-between items-center"
                {% elif message.tags == 'error' %}
                    class="bg-red-700 text-white mx-5 my-2 md:mx-auto md:w-[50rem] p-4 rounded flex justify-between items-center"
                {% else %}
